function abreOuFechaResposta(t){t.target.parentNode.classList.toggle("faq__questions__item--is-open")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var t=t.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`),e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("movies__content--is-active");t.classList.add("movies__content--is-active")});for(let t=0;t<a.length;t++)a[t].addEventListener("click",abreOuFechaResposta)});